import React, { useState } from 'react'
import axios from 'axios'

const PostForm = () => {
    const [formData, setFormData] = useState({
        title: '',
        user: '',
        genre: '',
        like: 0
      });

      const handleChange = (e) => {
        setFormData({
          ...formData,
          [e.target.name]: e.target.value
        });
      };

      const handleSubmit = (e) => {
        e.preventDefault();

        axios.post('/api/posts', formData)
          .then(response => {
            console.log('Post successful:', response.data);
            // Reset form fields after successful post if needed
            setFormData({
                title: '',
                user: '',
                genre: '',
                like: 0
            });
          })
          .catch(error => {
            console.error('Error:', error);
          });
      };

  return (
    <div className="mx-auto p-5 my-3 bg-gray-300 shadow-[0px_0px_10px_0px_#1e1b4b] rounded-xl bg-clip-padding backdrop-filter backdrop-blur-lg bg-opacity-10 border border-gray-700 text-white w-10/12 self-center">
      <h2 className='text-3xl'>Create Post</h2>
      <form onSubmit={handleSubmit} className='p-3 gap-5'>
        <div>
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="user">User:</label>
          <textarea
            id="user"
            name="user"
            value={formData.user}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="genre">Genre:</label>
          <textarea
            id="genre"
            name="genre"
            value={formData.genre}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="like">Like:</label>
          <textarea
            id="like"
            name="like"
            value={formData.like}
            onChange={handleChange}
          />
        </div>
        <button type='submit' class="inline-flex items-center justify-center p-0.5 mb-2 me-2 overflow-hidden text-sm font-medium text-gray-900 rounded-lg group bg-gradient-to-br from-purple-600 to-blue-500 group-hover:from-purple-600 group-hover:to-blue-500 hover:text-white dark:text-white focus:ring-4 focus:outline-none focus:ring-blue-300 dark:focus:ring-blue-800">
            <span class="relative px-5 py-2.5 transition-all ease-in duration-75 bg-white dark:bg-gray-900 rounded-md group-hover:bg-opacity-0">
            Submit
            </span>
        </button>
      </form>
    </div>
  )
}

export default PostForm
