import React, { useState } from 'react'
import axios from 'axios'

const PostForm = () => {
    const [formData, setFormData] = useState({
        title: '',
        user: '',
        genre: '',
        like: 0
      });

      const handleChange = (e) => {
        setFormData({
          ...formData,
          [e.target.name]: e.target.value
        });
      };

      const handleSubmit = (e) => {
        e.preventDefault();

        axios.post('/api/posts', formData)
          .then(response => {
            console.log('Post successful:', response.data);
            // Reset form fields after successful post if needed
            setFormData({
                title: '',
                user: '',
                genre: '',
                like: 0
            });
          })
          .catch(error => {
            console.error('Error:', error);
          });
      };

  return (
    <div className="mx-auto p-5 my-3 bg-gray-300 shadow-[0px_0px_10px_0px_#1e1b4b] rounded-xl bg-clip-padding backdrop-filter backdrop-blur-lg bg-opacity-10 border border-gray-700 text-white w-10/12 self-center">
        <form>
        <h2 className='text-3xl mb-5'>Create Post</h2>
        <div class="grid gap-6 mb-6 md:grid-cols-2 p-3">
        <div>
            <label for="title" class="block mb-2 text-lg font-medium text-gray-900 dark:text-white">Title</label>
            <input type="text" id="title" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="An amazing song idea" required />
        </div>
        <div>
            <label for="phone" class="block mb-2 text-lg font-medium text-gray-900 dark:text-white">Artist Name</label>
            <input type="text" id="phone" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="Artist Name" required />
        </div>
        <div>
            <label for="genre" class="block mb-2 text-lg font-medium text-gray-900 dark:text-white">Genre</label>
            <input type="text" id="last_name" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="House" required />
        </div>
        <div>
            <label for="company" class="block mb-2 text-lg font-medium text-gray-900 dark:text-white">SoundCloud link</label>
            <input type="text" id="company" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="https://on.soundcloud.com/example" required />
            <p id="helper-text-explanation" class="mt-2 text-sm text-gray-500 dark:text-gray-400">Please make sure you use the correct private link. Here is a short <a href="#" class="font-medium text-blue-600 hover:underline dark:text-blue-500">guide</a>.</p>
        </div>  
        <div>
        <label for="message" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your message</label>
            <textarea id="message" rows="4" class="block p-2.5 w-full text-sm text-gray-900 bg-gray-50 rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="A short description"></textarea>    
        </div> 
    </div>
    </form>
      <form onSubmit={handleSubmit} className='p-3 gap-5 flex flex-col'>
        <div>
          <label className='' htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="user">User:</label>
          <input
            type='text'
            id="user"
            name="user"
            value={formData.user}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="genre">Genre:</label>
          <input
            type='text'
            id="genre"
            name="genre"
            value={formData.genre}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="like">Like:</label>
          <input
            type='text'
            id="like"
            name="like"
            value={formData.like}
            onChange={handleChange}
          />
        </div>
        <button type='submit' class="self-center inline-flex items-center justify-center p-0.5 mb-2 me-2 overflow-hidden text-sm font-medium text-gray-900 rounded-lg group bg-gradient-to-br from-purple-600 to-blue-500 group-hover:from-purple-600 group-hover:to-blue-500 hover:text-white dark:text-white focus:ring-4 focus:outline-none focus:ring-blue-300 dark:focus:ring-blue-800">
            <span class="relative text-xl px-5 py-2.5 transition-all ease-in duration-75 bg-white dark:bg-gray-900 rounded-md group-hover:bg-opacity-0">
            Submit
            </span>
        </button>
      </form>
    </div>
  )
}

export default PostForm
