import React from 'react'

const PostDetails = ({post}) => {
  return (
    <div className="p-5 my-3 bg-gray-300 shadow-[0px_0px_10px_0px_#1e1b4b] rounded-xl bg-clip-padding backdrop-filter backdrop-blur-lg bg-opacity-10 border border-gray-700 text-white w-10/12 self-center">
    <div className="flex flex-row items-center justify-between">
        {/* Cover Image */}
        <img className="w-4/12 sm:w-1/6 md:w-2/12 lg:w-2/12 rounded-md" src="https://picsum.photos/200" alt="" />

        {/* Details */}
        <div className="flex flex-col justify-between gap-0.5 sm:gap-1 md:gap-2 lg:gap-4 xl:gap-6 items-start w-full h-full px-5">
            <p class="font-semibold text-lg sm:text-xl md:text-2xl lg:text-3xl xl:text-5xl">Song Title</p>
            <p class="font-light text-sm sm:text-sm md:text-lg lg:text-xl xl:text-2xl">Artist</p>
            <p class="font-extralight text-sm sm:text-sm md:text-sm lg:text-lg">Genre</p>
        </div>

        {/* Interactions */}
        <div class="flex flex-col justify-around items-center gap-4">
            {/* Comment */}
            <svg xmlns="http://www.w3.org/2000/svg" width="25" height="25" viewBox="0 0 24 24" fill="none" stroke="#ffffff" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><path d="M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z"></path></svg>

            {/* Like */}
            <div class="flex flex-col items-center gap-1.5">
                <svg class="transition-all duration-300 hover:fill-white hover:cursor-pointer" xmlns="http://www.w3.org/2000/svg" width="25" height="25" viewBox="0 0 24 24" fill="none" stroke="#ffffff" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><path d="M20.84 4.61a5.5 5.5 0 0 0-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 0 0-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 0 0 0-7.78z"></path></svg>
                <p class="text-sm">13</p>
            </div>
        </div>

    </div>
    </div>
  )
}

export default PostDetails
